{"changed":true,"filter":false,"title":"callback.php","tooltip":"/Front/Erlang - Php Nodes/callback.php","value":"<?php\n\n\n//Let's start by creating the open callback\n\nclass CallbackA {\npublic function open($save_path, $session_name)\n{\n  if(null === $this->link)\n  {\n    $this->link = peb_connect($this->host, $this->erlang_cookie, $this->conn_timeout);\n    if(!$this->link)\n    {\n      throw new Exception(sprintf(\"Can't connect to the erlang node %s using erlang_cookie %s\", $this->host, $this->erlang_cookie));\n    }\n  }\n  return $this->link;\n}\n\n\n\n//function peb_connect that expects three parameters, the host to connect to, the Erlang secret cookie and an optional connection timeout.\n\nprotected $host = 'server@127.0.0.1';\nprotected $erlang_cookie = 'ABCDEFGHI';\nprotected $conn_timeout = 5;\n\n\n//Close session\npublic function close()\n{\n  if(is_resource($this->link))\n  {\n    peb_close($this->link);\n  }\n}\n\n\n//Read\npublic function read($session_id)\n{\n  $x = peb_encode(\"[~s]\", array(array($session_id)));\n  $result = peb_rpc(\"session_handler\", \"read\", $x, $this->link);\n  $rs = peb_decode($result);\n  $data = $rs[0];\n  return is_array($data) ? '' : $data;\n}\n\n//Write\npublic function write($session_id, $session_data)\n{\n  $x = peb_encode(\"[~s, ~s]\", array(array($session_id, $session_data)));\n  $result = peb_rpc(\"session_handler\", \"write\", $x, $this->link);\n  unset($result);\n  return true;\n}\n\n//Destroy\npublic function destroy($session_id)\n{\n  $x = peb_encode(\"[~s]\", array(array($session_id)));\n  $result = peb_rpc(\"session_handler\", \"destroy\", $x, $this->link);\n  unset($result);\n  return true;\n}\n\n//Handler \npublic function gc($max_expire_time)\n{\n  $x = peb_encode('[~i]', array(array($max_expirte_time)));\n  $result = peb_rpc(\"session_handler\", \"gc\", $x, $this->link);\n  $rs = peb_decode($result);\n  return $rs;\n}\n\n//use class as a session handler\n\n$sh = new ErlangSessionHandler();\n\nsession_set_save_handler(\narray($sh,\"open\"),\narray($sh,\"close\"),\narray($sh,\"read\"),\narray($sh,\"write\"),\narray($sh,\"destroy\"),\narray($sh,\"gc\")\n);\n\nsession_start();\n\n}","undoManager":{"mark":93,"position":100,"stack":[[{"start":{"row":47,"column":0},"end":{"row":48,"column":0},"action":"insert","lines":["",""],"id":97}],[{"start":{"row":48,"column":0},"end":{"row":48,"column":1},"action":"insert","lines":["/"],"id":98}],[{"start":{"row":48,"column":1},"end":{"row":48,"column":2},"action":"insert","lines":["/"],"id":99}],[{"start":{"row":48,"column":2},"end":{"row":48,"column":3},"action":"insert","lines":["w"],"id":100}],[{"start":{"row":48,"column":3},"end":{"row":48,"column":4},"action":"insert","lines":["r"],"id":101}],[{"start":{"row":48,"column":4},"end":{"row":48,"column":5},"action":"insert","lines":["i"],"id":102}],[{"start":{"row":48,"column":5},"end":{"row":48,"column":6},"action":"insert","lines":["t"],"id":103}],[{"start":{"row":48,"column":6},"end":{"row":48,"column":7},"action":"insert","lines":["e"],"id":104}],[{"start":{"row":48,"column":7},"end":{"row":48,"column":8},"action":"insert","lines":[" "],"id":105}],[{"start":{"row":48,"column":7},"end":{"row":48,"column":8},"action":"remove","lines":[" "],"id":106}],[{"start":{"row":48,"column":6},"end":{"row":48,"column":7},"action":"remove","lines":["e"],"id":107}],[{"start":{"row":48,"column":5},"end":{"row":48,"column":6},"action":"remove","lines":["t"],"id":108}],[{"start":{"row":48,"column":4},"end":{"row":48,"column":5},"action":"remove","lines":["i"],"id":109}],[{"start":{"row":48,"column":3},"end":{"row":48,"column":4},"action":"remove","lines":["r"],"id":110}],[{"start":{"row":48,"column":2},"end":{"row":48,"column":3},"action":"remove","lines":["w"],"id":111}],[{"start":{"row":48,"column":2},"end":{"row":48,"column":3},"action":"insert","lines":["W"],"id":112}],[{"start":{"row":48,"column":3},"end":{"row":48,"column":4},"action":"insert","lines":["r"],"id":113}],[{"start":{"row